import { useParams } from "react-router-dom";
import hotelList from "../../data/hotel_list.json";
import Navbar from "../../components/navbar";
import Footer from "../../components/footer";
import "./detail.css";

const Detail = () => {
  const { hotelName } = useParams(); //read parameters from the URL.
  const hotel = hotelList.find(
    (h) => h.name === decodeURIComponent(hotelName)
  );

  if (!hotel) return <h2>Hotel not found</h2>;

  return (
    <div className="detail-container">
      <div className="detail-header">
        <h1>{hotel.name}</h1>
        <button className="book-btn">Reserve or Book Now!</button>
      </div>

      <p className="detail-location">
        <i className="fa fa-map-marker" /> {hotel.city}
      </p>

      <p className="detail-offer">
        Book a stay over ${hotel.price} and get a free airport taxi!
      </p>

      <div className="detail-gallery">
        {[...Array(6)].map((_, i) => (
          <img
            key={i}
            src={hotel.image_url}
            alt={`${hotel.name} ${i + 1}`}
          />
        ))}
      </div>

      <div className="detail-info">
        <div className="detail-description">
          <h2>Stay in the heart of {hotel.city}</h2>
          <p>
            Located a short walk from city center, {hotel.name} offers
            accommodations with air conditioning and free Wi-Fi. The units come
            with hardwood floors, flat-screen TVs, private bathrooms with
            showers, and fully equipped kitchenettes. Guests can enjoy coffee
            machines, microwaves, and hairdryers.
          </p>
        </div>

        <div className="detail-sidebar">
          <div className="detail-sidebar-box">
            <h3>Perfect for a 9-night stay!</h3>
            <p>
              This property is located in the real heart of {hotel.city} and has
              an excellent location score of {hotel.rate}.
            </p>
            <h2>${hotel.price * 9} (9 nights)</h2>
            <button className="book-btn">Reserve or Book Now!</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Detail;
