import React from "react";
import './header.css';

function Header() {
    const handleSearch = () => {
        // when clicking Search, redirect to /search
        window.location.replace("/search");
    };

    return (
        <header className="header">
            <h1>A life time of discount? It's genius.</h1>
            <h3>Get rewarded for your travel - unlock instant savings of 10% or more with a free account</h3>
            <div className="header-form">
                <div className="formItem">
                    <i className="fa fa-bed"></i>
                    <input className="header-input" type="text" placeholder="Where are you going?" />
                </div>

                <div className="formItem date-range"> {/* NEW - replaced single date input */}
                    <i className="fa fa-calendar"></i>
                    <input type="date" placeholder="Check-in" /> {/* NEW */}
                    <span className="date-separator">to</span> {/* NEW */}
                    <input type="date" placeholder="Check-out" /> {/* NEW */}
                </div>

                <div className="formItem guest-selector">
                    <i className="fa fa-user"></i>
                    <input
                        type="text"
                        className="header-input"
                        value={`${adults} adults 路 ${children} children 路 ${rooms} room`}
                        readOnly
                        onClick={() => setOpen(!open)}
                    />
                    {open && (
                        <div className="guest-popup">
                            <div className="guest-row">
                                <span>Adults</span>
                                <button onClick={() => setAdults(Math.max(1, adults - 1))}>
                                    -
                                </button>
                                <span>{adults}</span>
                                <button onClick={() => setAdults(adults + 1)}>+</button>
                            </div>
                            <div className="guest-row">
                                <span>Children</span>
                                <button onClick={() => setChildren(Math.max(0, children - 1))}>
                                    -
                                </button>
                                <span>{children}</span>
                                <button onClick={() => setChildren(children + 1)}>+</button>
                            </div>
                            <div className="guest-row">
                                <span>Rooms</span>
                                <button onClick={() => setRooms(Math.max(1, rooms - 1))}>
                                    -
                                </button>
                                <span>{rooms}</span>
                                <button onClick={() => setRooms(rooms + 1)}>+</button>
                            </div>
                        </div>
                    )}
                </div>

                <button onClick={handleSearch}>Search</button>
            </div>
        </header>
    );
}

export default Header;
